import random
random.randint(1, 10)    # สุ่มจำนวนเต็มระหว่าง 1 ถึง 10
random.random()          # สุ่มทศนิยมระหว่าง 0 ถึง 1
random.choice([1, 2, 3]) # สุ่มเลือกสมาชิกจากลิสต์
random.shuffle([1, 2, 3, 4]) # สุ่มสับเปลี่ยนลำดับในลิสต์

abs(-5)                  # หาค่าสัมบูรณ์ 5
round(3.14159, 2)        # ปัดเศษทศนิยมให้มี 2 ตำแหน่ง 3.14
pow(2, 3)                # ยกกำลัง 2^3 = 8
divmod(9, 4)             # หาผลหารและเศษ (9 // 4, 9 % 4) = (2, 1)

sum([1, 2, 3])           # หาผลรวมของลิสต์ 6
min(1, 2, 3)             # หาค่าต่ำสุด 1
max(1, 2, 3)             # หาค่าสูงสุด 3

import math
math.sqrt(16)            # หารากที่สอง 4.0
math.sin(math.pi / 2)    # หาค่า sine ของมุม 90 องศา 1.0
math.cos(0)              # หาค่า cosine ของมุม 0 องศา 1.0
math.exp(1)              # หาค่า e^1 = 2.718...
math.log(10, 10)         # หาลอการิทึมฐาน 10 ของ 10 1.0
math.log10(100)          # หาลอการิทึมฐาน 10 ของ 100 2.0
math.factorial(5)        # หาแฟกทอเรียลของ 5 (5!) = 120


bin(10)                  # แปลงเป็นเลขฐานสอง '0b1010'
oct(10)                  # แปลงเป็นเลขฐานแปด '0o12'
hex(10)                  # แปลงเป็นเลขฐานสิบหก '0xa'
int('0b1010', 2)         # แปลงจากเลขฐานสองเป็นจำนวนเต็ม 10
int('0xa', 16)           # แปลงจากเลขฐานสิบหกเป็นจำนวนเต็ม 10

int("123")               # แปลงสตริงเป็นจำนวนเต็ม 123
float("123.45")          # แปลงสตริงเป็นทศนิยม 123.45
complex(1, 2)            # สร้างจำนวนเชิงซ้อน 1 + 2j
int(123.45)              # แปลงทศนิยมเป็นจำนวนเต็ม 123
float(123)               # แปลงจำนวนเต็มเป็นทศนิยม 123.0

(10).bit_length()        # หาจำนวนบิตที่ใช้แทนค่า 10 ใน binary 4 (10 = 1010)
(15).to_bytes(2, 'big')  # แปลง 15 เป็น bytes ในรูปแบบ 'big-endian' b'\x00\x0f'
int.from_bytes(b'\x00\x0f', 'big')  # แปลง bytes กลับเป็นจำนวนเต็ม 15






